	/** Connexion rapide à Ogame *
	 *****************************/
	function easyRegistration(){
		var loginsSaved = GM_getValue('logins', ',,|').split('|');
		var login = [];
		var pass = [];
		var servor = [];
		var options = '';
		for(var i=0; i<loginsSaved.length-1; i++){
			login[i] = loginsSaved[i].split(',')[0];
			pass[i] = (loginsSaved[i].split(',')[1] == 'N/A' ? '' : loginsSaved[i].split(',')[1]);
			servor[i] = loginsSaved[i].split(',')[2];
			options += '<option value="'+i+'"> '+login[i]+' </option>';
		}
		document.getElementById('login').style.display = 'block';
		document.getElementById('loginForm').innerHTML = '<div class="input-wrap">'
															+'<label for="idLogin"> Comptes: </label>'
																+'<div class="black-border">'
																	+'<select id="idLogin" class="js_uniUrl" name="login" style="width:188px;color:#30576F">'
																		+options
																+'</div>'
															+'</label>'
														+ '</div>'
														+ document.getElementById('loginForm').innerHTML;
														
		for(i=0; i<loginsSaved.length-1; i++){
			document.getElementById('idLogin').options[i].addEventListener("click", function(event){fillingLogin(this.value);}, true);
		}
		fillingLogin(0);
	}
	
	/** Remplissage automatique des identifiants *
	 *********************************************/
	function fillingLogin(num){
		var loginsSaved = GM_getValue('logins', ',,|').split('|');
		var login = [];
		var pass = [];
		var servor = [];
		var options = '';
		for(var i=0; i<loginsSaved.length-1; i++){
			login[i] = loginsSaved[i].split(',')[0];
			pass[i] = (loginsSaved[i].split(',')[1] == 'N/A' ? '' : loginsSaved[i].split(',')[1]);
			servor[i] = loginsSaved[i].split(',')[2];
			options += '<option value="'+i+'"> '+login[i]+' </option>';
		}
		var serveurLogin = document.getElementById('serverLogin');
		//alert(servor[0]);
		for (i in serveurLogin.options){
			if (serveurLogin.options[i].value == servor[parseInt(num)]){
				serveurLogin.options[i].selected = true;
				i=-1;
			}
		}
		document.getElementById('usernameLogin').value = login[parseInt(num)];
		document.getElementById('passwordLogin').value = pass[parseInt(num)];
		// document.getElementById('loginSubmit').click();
	}
	
	/** Séparateur de millier  ==> by Vulca <== *
	 ********************************************/
	function addPoints(nombre){
		if(isNaN(nombre) === true) return nombre;
		else {
			var signe = '';
			if (nombre < 0){
				nombre = Math.abs(nombre);
				signe = '-';
			}
			nombre = parseInt(nombre, 10);
			var str = nombre.toString(), n = str.length;
			if (n <4){
				return signe + nombre;
			}
			else
				return signe + (((n % 3) ? str.substr(0, n % 3) + '.' : '') + str.substr(n % 3).match(new RegExp('[0-9]{3}', 'g')).join('.'));
		}
	}

	function colorBackgroundInput(num, type){
		if(type == 'text'){
			var newInputText = document.getElementById('colorText'+num).value.replace(/[^0-9a-fA-F]/g, '').toUpperCase();
			if(newInputText.length == 6){
				if(parseInt(num, 10) < 2){
					document.getElementsByClassName('qte_x')[parseInt(num, 10)].style.color = '#'+newInputText;
				}
				document.getElementById('colorText'+num).style.backgroundColor = "#"+newInputText;
				document.getElementById('colorPicker'+num).value = "#"+newInputText;
			}
			document.getElementById('colorText'+num).value = newInputText;
		}
		else if(type == 'picker'){
			var newInputColor = document.getElementById('colorPicker'+num).value.replace(/[^0-9a-fA-F]/g, '').toUpperCase();
			if(newInputColor.length == 6){
				if(parseInt(num, 10) < 2){
					document.getElementsByClassName('qte_x')[parseInt(num, 10)].style.color = '#'+newInputColor;
				}
				document.getElementById('colorText'+num).value = newInputColor;
				document.getElementById('colorText'+num).style.backgroundColor = "#"+newInputColor;
			}
			document.getElementById('colorPicker'+num).value = "#"+newInputColor;
		}
	}

	function trim(string){
		return string.replace(/(^\s*)|(\s*$)/g,'');
	}

	function replace_check(bool){
		if (bool == "true" || bool === true ){
			return "checked";
		}
		else
			return "unchecked";
	}

