	function display(body){
		var cellule = [id('BODY_RES'), id('BODY_FLO_CS'), id('BODY_FLO_BS'), id('BODY_BUI'), id('BODY_OPT')];

		for ( var i=0; i<cellule.length; i++ ){
			cellule[i].style.display = 'none';
		}
		id(body).style.display = '';

		if (id('BODY_RES').style.display === ''){
			activeInterval(EmpireCalculator, 0, 2000);
		}
		else
			stopInterval(0);
		if (id('BODY_OPT').style.display == 'none'){
			stopInterval(1);
		}
	}

	function activeInterval(fonction, indice, time){
		if (interval[indice] === ''){
			interval[indice] = setInterval(fonction, time);	//fadeBoxx('activeInterval : '+interval[0]+' time : '+time, true, 1000);
		}
	}

	function stopInterval(indice){
		if (interval[indice] != ''){
			var clr = clearInterval(interval[indice]);
			interval[indice] = '';	//fadeBoxx('stopInterval '+indice, true, 1000);
		}
	}

	function activeDelay(fonction, indice, time){
		stopInterval(0);
		interval[indice] = setTimeout(fonction, time);
	}

